parameters.param:
echo "BranchToStartVA %env.BranchToStartVA%" echo "teamcity.pullRequest.target.branch %teamcity.pullRequest.target.branch%" echo "teamcity.build.branch %teamcity.build.branch%"
if('%teamcity.pullRequest.target.branch%' -eq '%env.BranchToStartVA%') { $StartVA = 'true' $SonarParamCover = "-Dsonar.coverageReportPaths=%system.teamcity.build.workingDir%/Cover.xml" $InfobaseName = "%env.Database1CName%_%teamcity.pullRequest.target.branch%_pull%teamcity.pullRequest.number%" } elseif('%teamcity.build.branch%' -eq '%env.BranchToStartVA%') { $StartVA = 'true' $SonarParamCover = "-Dsonar.coverageReportPaths=%system.teamcity.build.workingDir%/Cover.xml" $InfobaseName = "%env.Database1CName%_%teamcity.build.branch%"
} else { $StartVA = 'false' $SonarParamCover = "" $InfobaseName = "%env.Database1CName%_%teamcity.build.branch%" }
$InfobaseName = $InfobaseName -replace "\\", "_" $InfobaseName = $InfobaseName -replace "\/", "_" $InfobaseName = $InfobaseName -replace "\-", "_"
echo "##teamcity[setParameter name='env.Database1CName' value='$InfobaseName']" echo "##teamcity[setParameter name='env.SonarParamCover' value='$SonarParamCover']" echo "##teamcity[setParameter name='env.StartVA' value='$StartVA']"
